mapper.insert:
INSERT INTO Notice_File(notice_id, path, saved_name, type) VALUES
(#{model.noticeId}, #{model.path}, #{model.savedName}, #{model.type})
;

mapper.select:
SELECT n.id, u.name AS userId, n.thumbnail, n.title, n.content, n.created_at, n.updated_at, s.id AS scrapId FROM Notice n LEFT JOIN Notice_Target nt on n.id = nt.notice_id LEFT JOIN User u ON n.user_id = u.id LEFT JOIN Scrap s ON s.user_id = #{userId} and s.notice_id = n.id and s.is_deleted = 0 WHERE nt.target = #{department} AND nt.is_deleted = 0
AND n.id <= #{cursor}
AND n.title LIKE CONCAT('%',#{searchBy},'%')
AND n.is_deleted = 0 ORDER BY n.id desc LIMIT #{limits};
